public class LongestSubstringOfAllVowelsInOrder {

  public static void main(String[] args) {
    System.out.println(new LongestSubstringOfAllVowelsInOrder().longestBeautifulSubstring(""));
  }

  public int longestBeautifulSubstring(String word) {
    char[] vowels = {'a', 'e', 'i', 'o', 'u'};
    char[] chars = word.toCharArray();
    int max = 0;

    for (int i = 0; i < chars.length; i++) {
      int curr = 0;
      int currVowel = 0;

      while (true) {
        boolean foundOne = false;

        while (i < chars.length && chars[i] == vowels[currVowel]) {
          curr++;
          foundOne = true;
          i++;
        }

        if (!foundOne) {
          break;
        }

        currVowel++;

        if(currVowel >= vowels.length) {
          max = Math.max(max, curr);
          break;
        }
      }

      if(currVowel > 0) {
        i --;
      }
    }

    return max;
  }

}
